mapper.select:
select class_curr_nop as classCurrNop
from cooking_class 
where class_no=#{classNo}

mapper.insert:
insert into cooking_class values(
	class_seq.nextval, #{memberNickname}, #{classTitle}, #{classContent}, #{classStart}, #{classEnd}, #{classPrice}, 0, #{classNop}, 0, 1, to_char(sysdate, 'YYYY-MM-DD'), #{classStartTime}, #{classEndTime}, #{classLocation1}, #{classLocation2}
)

mapper.update:
update cooking_class 
set class_title=#{classTitle}, class_content=#{classContent}, class_start=#{classStart}, class_end=#{classEnd}, class_price=#{classPrice}, class_nop=#{classNop}, class_start_time=#{classStartTime}, class_end_time=#{classEndTime}, class_location1=#{classLocation1}, class_location2=#{classLocation2}
where class_no=#{classNo}
