mapper.select:
select * from ( select rownum as rnum, memberNo,memberId,memberPw,memberEmail,memberName,memberNickname, memberPhone,memberBirth, postcode,addressRoad,addressDetail,memberConsent,profilePath,profileIntro, certificatePath,memberLevel,memberPoint,enrollDate,withdrawDate,memberBlack, boardCount,recipeCount, fcCount,rc from (select member_no as memberNo, member_id as memberId, member_pw as memberPw, member_email as memberEmail, member_name as memberName, member_nickname as memberNickname, member_phone as memberPhone, member_birth as memberBirth, postcode, address_road as addressRoad, address_detail as addressDetail, member_consent as memberConsent, profile_path as profilePath, profile_intro as profileIntro, certificate_path as certificatePath, member_level as memberLevel, member_point as memberPoint, enroll_date as enrollDate, withdraw_date as withdrawDate, member_black as memberBlack, (select count(*) from freeboard where free_writer = member_id)boardCount, (select count(*) from recipe_board where recipe_writer = member_no)recipeCount, (select count(*) from freeboard_comment where fc_writer = member_id)fcCount , (select report_comment from (member)m1 join recipe_board on(member_no=recipe_writer) join recipe_report using(recipe_no) where member.member_no = m1.member_no and rownum=1 )rc from member order by enrollDate desc) where memberBlack = #{level}
and ${searchType} like '%'||#{searchText}||'%'
and
boardCount
recipeCount
fcCount
(enrollDate
>= #{searchText2}
<= #{searchText2}
between #{joinStart} and #{joinEnd})
and memberLevel in (1,2)
and memberLevel =1
and memberLevel =2
and memberLevel = 4
and memberLevel = 5
) where rnum between #{start2} and #{end2}

select member_no as memberNo, member_id as memberId, member_pw as memberPw, member_email as memberEmail, member_name as memberName, member_nickname as memberNickname, member_phone as memberPhone, member_birth as memberBirth, postcode, address_road as addressRoad, address_detail as addressDetail, member_consent as memberConsent, profile_path as profilePath, profile_intro as profileIntro, certificate_path as certificatePath, member_level as memberLevel, member_point as memberPoint, enroll_date as enrollDate, withdraw_date as withdrawDate, member_black as memberBlack from member
where member_id=#{memberId} and member_pw=#{memberPw}
where member_no=#{memberNo}

select member_no as memberNo
from member

	
		where member_id=#{memberId}
	
	
		where member_email=#{memberEmail}
	
	
		where member_nickname=#{memberNickname}
	
	
		where member_phone=#{memberPhone}

select member_no as memberNo,
		member_id as memberId
from member

	
		where member_name=#{memberName} and member_email=#{memberEmail}
	
	
		where member_id=#{memberId} and member_email=#{memberEmail}
