mapper.select:
select rrr.* from (select rownum as rnum, rr.*, m.member_nickname as memberNickname, m.profile_path as profilePath from (select cr.contest_no as contestNo, cr.RECIPE_NO as recipeNo, cr.ENTER_DATE as enterDate, cr.CONTEST_RESULT as contestResult, cr.CONTEST_MONTH as contestMonth, cr.RECIPE_DELETE as recipeDelete, r.recipe_writer, r.recipe_title as recipeTitle, r.subhead as recipeContent, r.filepath as recipePath, (select count(*) from contest_vote where contest_no=cr.contest_no) as voteCount from contest_recipe cr left join recipe_board r on cr.RECIPE_NO=r.recipe_no
order by contestNo desc
order by recipeTitle
order by voteCount desc
)rr left join member m on rr.recipe_writer=m.member_no)rrr where rnum between #{start} and #{end}
